import { Component } from "@angular/core";
import { FormGroup, FormControl, Validators, FormBuilder, ReactiveFormsModule } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: "app-items-itemform",
  templateUrl: "./itemform.component.html"
})
export class ItemformComponent {

  itemForm: FormGroup;

  constructor(private _formbuilder: FormBuilder, private _router: Router) {
    this.itemForm = _formbuilder.group({
      name: ['', Validators.required],
      price: [0, Validators.required],
      description: [''],
      imageUrl: ['']
    });
  }

  onSubmit() {
    console.log("ItemCreate form submitted:");
    console.log(this.itemForm);
    console.log('The item ' + this.itemForm.value.name + ' is created.');
    console.log(this.itemForm.touched);
  }

  backToItems() {
    this._router.navigate(['/items']);
  }
}
<h2>Create Item</h2>

<form [formGroup]="itemForm" (ngSubmit)="onSubmit()">
  <div class="form-group">
    <label for="name">Name:</label>
    <input type="text" id="name" formControlName="name" class="form-control" />
  </div>
  <div class="form-group">
    <label for="price">Price:</label>
    <input type="number" id="price" formControlName="price" class="form-control" />
  </div>
  <div class="form-group">
    <label for="description">Description:</label>
    <textarea id="description" formControlName="description" class="form-control"></textarea>
  </div>
  <div class="form-group">
    <label for="imageUrl">Image URL:</label>
    <input type="text" id="imageUrl" formControlName="imageUrl" class="form-control" />
  </div>
  <button class='btn btn-primary' type="submit" [disabled]="itemForm.invalid">Create Item</button>
  <button class="btn btn-secondary" (click)="backToItems()">Cancel</button>
</form>
